// Generated by CoffeeScript 1.6.3
(function() {
  var vm_login;

  vm_login = new Vue({
    el: '#login-form',
    data: {
      user: '',
      pwd: ''
    },
    methods: {
      login: function() {
        if (this.user && this.pwd) {
          return $.ajax({
            url: '/j/admin/login',
            data: this.$data,
            method: 'POST',
            success: function(o) {
              if (o.result) {
                vm_login.user = '';
                vm_login.pwd = '';
                return location.href = '/admin/index';
              } else {
                return alert(o.msg);
              }
            },
            error: function() {}
          });
        }
      }
    }
  });

}).call(this);
